/**
 * Type declarations for Quick-UI React CSS imports
 *
 * This file allows TypeScript to understand CSS imports from quick-ui-react:
 * - import "quick-ui-react/styles"
 * - import "quick-ui-react/dist/style.css"
 */

declare module "quick-ui-react/styles" {
  const content: void;
  export default content;
}

declare module "quick-ui-react/dist/style.css" {
  const content: void;
  export default content;
}
